ValidationRule.errorConditionFormula:
AND(
				OR(
								ISNEW(),
								ISCHANGED( Name )
				),
				TEXT(Tipo__c)='Periodico',
				NOT(ISBLANK (
								VLOOKUP($ObjectType.CBK_Framework_Batch__c.Fields.Name,
																$ObjectType.CBK_Framework_Batch__c.Fields.Name, 
																Name 
															)
				    )
					)
)
