CustomField.formula:
IF(ISPICKVAL(Status , 'Pendiente Colaborador'),
	(NOW()-CC_Fecha_Traslado_Colaborador__c)*24,
	IF(ISPICKVAL(Status , 'Pendiente Interno'),
		(NOW()-CC_Fecha_Traslado_3N__c)*24,
		IF(ISPICKVAL(Status , 'Pendiente Incidencia'),
			(NOW()-CC_Agrupador_Id__r.CC_Fecha_creacion_MAXIMO__c)*24,
			null
		)
	)
)
