CustomField.formula:
IF(
    NOT(
    ISBLANK(CC_Fecha_Twitter_Entrante__c)
    ),
    IF(
        NOT(
        ISBLANK(CC_Fecha_1a_Respuesta_Twitter__c)
        ),
        (CC_Fecha_1a_Respuesta_Twitter__c - CC_Fecha_Twitter_Entrante__c)*24*60,
        IF(
            NOT(
            ISBLANK(ClosedDate)
            ),
            (ClosedDate - CC_Fecha_Twitter_Entrante__c)*24*60,
            (NOW() - CC_Fecha_Twitter_Entrante__c)*24*60
        )
    ),
    IF(
        OR(ISPICKVAL(Status,"Rechazado"), ISPICKVAL(Status,"Cerrado")),
        (ClosedDate - CreatedDate)*24*60,
        (NOW() - CreatedDate)*24*60
    )
)
