ValidationRule.errorConditionFormula:
AND(NOT($Permission.SAC_ByPass),
				 RecordType.DeveloperName == 'SAC_Reclamacion',
					 OR( AND(ISCHANGED(Status), ISPICKVAL(Status, 'SAC_002'), ISBLANK(TEXT( SAC_Naturaleza__c ))) ,
								  AND(ISCHANGED(SAC_Naturaleza__c), NOT(ISPICKVAL(Status, 'SAC_001')), ISBLANK(TEXT( SAC_Naturaleza__c ))) 
							) 
				 )
