CustomField.formula:
IF( RecordType.DeveloperName = 'SAC_PreAlta',
    SAC_Caso__r.CreatedDate -SAC_Caso__r.SAC_FechaRecepcion__c,

        IF(ISBLANK(SAC_FechaInicio__c), 
				        	SAC_SumaDuracion__c, 
			        IF(AND(RecordType.DeveloperName = 'SAC_TMEGlobal', NOT(ISBLANK(SAC_Caso__r.OS_Fecha_Resolucion__c))), 
			            SAC_SumaDuracion__c + (    SAC_Caso__r.OS_Fecha_Resolucion__c  -  SAC_FechaInicio__c ),
																		SAC_SumaDuracion__c + ( NOW() -  SAC_FechaInicio__c )
																		)
			)


)
